import React, { useState } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

function App() {
  const [markers, setMarkers] = useState([]);
  const [distance, setDistance] = useState(0);
  const [fare, setFare] = useState(null);
  const [discount, setDiscount] = useState(false);

  function onMapClick(e) {
    if (markers.length >= 2) {
      setMarkers([]);
    }
    setMarkers([...markers, e.latlng]);
  }

  function MapEvents() {
    useMapEvents({
      click: onMapClick,
    });
    return null;
  }

  async function calculateFare(e) {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3000/calculate_fare', {
        distance,
        discount,
      });
      setFare(response.data.fare);
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <div>
      <header className="navbar">
        <h1 className="navbar">Tuyok</h1>
        <p className="navbar">Fare Calculator Prototype</p>
      </header>
      <form id="fare-form" onSubmit={calculateFare}>
        <MapContainer center={[7.07, 125.61]} zoom={13} style={{ height: '600px' }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <MapEvents />
          {markers.map((marker, index) => (
            <Marker key={index} position={marker} />
          ))}
        </MapContainer>
        <div className="discount">
          <input
            type="checkbox"
            id="discount"
            name="discount"
            checked={discount}
            onChange={() => setDiscount(!discount)}
          />
          <label htmlFor="discount">
            <span></span>Are you a student, PWD, or senior citizen?
          </label>
        </div>
        <button type="submit" className="submit">
          Calculate Fare
        </button>
      </form>
      {fare && <p id="fare-result">{fare}</p>}
    </div>
  );
}

export default App;
